const raw = $json.output || $json.message || $json.text;

if (!raw) {
  throw new Error("Missing AI output text.");
}

const match = raw.match(/```json\s*([\s\S]*?)```/);
if (!match) {
  throw new Error("No ```json code block found.");
}

const jsonString = match[1].trim();

try {
  const parsed = JSON.parse(jsonString);

  // âœ… Return as a proper n8n item
  return [{ json: parsed }];
} catch (err) {
  throw new Error("Workflow JSON parse failed: " + err.message);
}
